#!/usr/bin/env node

const execSync = require("child_process").execSync;

const url = process.env['BASEURL'];
const baseurl = url.substring(0, url.indexOf("/", url.indexOf("/") + 2));
const session = process.env['SESSION'];
const pkgnameorId = process.env['PACKAGE'];

let resultObj = '\'No results found\'';

const setup = `
	copado -p 'Setting instance url'
    sfdx force:config:set instanceUrl=${baseurl} --global

    copado -p 'Creating Temp Project'
    sfdx force:project:create -n temp

    copado -p 'Getting Package Info'
`;
execSync(setup);

const listPackages = `    
    cd temp
    sfdx force:package:list -v ${session} --json
`;
const packages = JSON.parse(execSync(listPackages).toString());
const pkg = packages.result.find(
  (element) => element.Name == pkgnameorId || element.Id == pkgnameorId
);

if(pkg) {
  const listVersions = `
      cd temp
      sfdx force:package:version:list -v ${session} -p ${pkg.Id} --json
  `;

  const versions = JSON.parse(execSync(listVersions).toString());

  resultObj = JSON.stringify({ pkg: pkg, versions: versions.result });
}

const result = `
	copado -p 'Updating Results' -r '${resultObj}'
`;
execSync(result);

#!/usr/bin/env node

const { execSync } = require('child_process'),
    { packages, baseUrl, session, wait, apiVersion } = process.env,
    url = baseUrl.substring(0, baseUrl.indexOf('/', baseUrl.indexOf('/') + 2));

// EXECUTION

execute();

function execute() {
    try {
        setInstanceURL();
        installPackages();
    } catch (error) {
        execSync(showErrorCmd(error.toString()));
    }
}

// SCRIPT FUNCTIONS

function setInstanceURL() {
    execSync(`
        copado -p 'Setting instance url'
        sfdx force:config:set instanceUrl=${url} --global || (${showErrorCmd(`Error setting instance URL`)})
    `);
}

function installPackages() {
    let subscriberIds = [];
    const listOfPackages = JSON.parse(packages);
    if (!listOfPackages?.length) {
        throw 'Packages list to install is empty.';
    }
    listOfPackages.forEach(pkg => {
        if (pkg.id) {
            executePackageInstallCmd(pkg);
            subscriberIds.push(pkg.id);
        }
    });

    execSync(`copado -p 'Updating Results' -r ${subscriberIds}`);
}

function executePackageInstallCmd(package) {
    let params = `-u ${session} -p ${package.id} -w ${wait}`;
    params += package.key ? ` -k '${package.key}'` : '';
    params += apiVersion ? ` --apiversion ${apiVersion}` : '';
    const installCmd = `sfdx force:package:install ${params} --json || true`;

    logger(`Package Install Command ==> ${installCmd}`);
    const response = JSON.parse(
        execSync(`
        copado -p 'Installing package ${package.id}'
        ${installCmd}
    `).toString()
    );

    logger(`Package Install Response ==> ${JSON.stringify(response)}`);
    if (response?.status) {
        throw `${response.name}: ${response.message}`;
    }
    execSync(`copado -p 'Package "${package.id}" successfully installed'`);
}

function showErrorCmd(error) {
    const suffix = 'Please check the logs for details';
    return `copado -p 'Error' -e '${error}, ${suffix}' && exit 1`;
}

function logger(text) {
    console.log(text);
}

