#!/usr/bin/env node
/**
 * Performs deploy of selected user story metadata changes.
 * Returns (If ACTION success) destination branch with merged changes of the user story metadata
 * (If ACTION failed) Returns details with error status on the job execution
 * @param file_changes_id
 * @param promotion
 * @param target_branch
 * @param destinationInstanceUrl
 * @param destinationSessionid
 * @param git_json
 * @param destination_env
 * @param find_and_replace_rules
 * @param conflict_resolution_attachments
 * @param deploy_id
 * @param isValidation
 * @param git_name
 * @param git_email
 * @param repository_id
 * @param testLevel
 * @param testClasses
 * @param testOnlyClasses
 * @param maxBuffer
 * @param sourceSessionid
 * @param sourceInstanceUrl
 * @param waitTime
*/

const { spawnSync } = require('child_process'),
    { writeFileSync, existsSync, readFileSync, rmSync, statSync } = require('fs'),
    { env, chdir, exit } = require('process'),
    sourceApiVersion = "55.0",
    encodedChangesFile = 'encoded_changes.json',
    { destinationSessionid, promotion, target_branch, isValidation, maxBuffer, destinationInstanceUrl} = env,
    targetDirectory = "/app/repository",
    ACTIONS = {
        ADD: 'add',
        RETRIEVE_ONLY: 'retrieveonly',
        FULL: 'full',
        DELETE: 'delete'
    },
    TYPES = {
        PERMISSION_SET: 'permissionset',
        PROFILE: 'profile'
    },
    STDIO = {
        INHERIT: 'inherit',
        PIPE: 'pipe',
        IGNORE: 'ignore'
    },
    MAXBUFFER = parseInt(maxBuffer);

let commitedMetadata = {};

// EXECUTION

execute();

function execute() {
    const { debugMode, target_branch, validationId } = env;
    let debugBranch;
    try {
        copadoPrepareMetadataChangeslList();
        getPromotionAndConfigureGit();
        gitMergePromotionToTarget();
        checkSFDXProjectJson();
        processFullProfiles();
        if(debugMode === "true") {
            debugBranch = `copadoDebug/${promotion?.split('/')[1]}`;
            createDebugPromotionBranch(debugBranch);
        }
        enrichChangeListAndProcessMetadata();
        replaceEnvironmentVariablesAndReplaceYAML();
        if(debugMode === "true") {
            pushDebugPromotionBranch(debugBranch);
        }
        setSFDXConfig(destinationInstanceUrl);
        if(isValidation === "false" && validationId) {
            executeQuickDeploy(validationId);
        }
        else {
            handleDeployResult(copadoStartDeployment());
        }
        cleanSfdxProjectConfigFiles();
        if(isValidation === "false") {   
            pushChangesToRemote(target_branch);
        }
    } catch (err) {
        executeCommand(getErrorCmdString(err.toString()));
        exit(1);
    }
}

// SCRIPT FUNCTIONS

function copadoPrepareMetadataChangeslList() {
    commitedMetadata.all = require(`/app/${encodedChangesFile}`);
    commitedMetadata = {
        ...commitedMetadata,
        add: commitedMetadata.all.filter(selectedMetadata => ((selectedMetadata.a.toLowerCase() == ACTIONS.ADD) || ( selectedMetadata.a.toLowerCase() == ACTIONS.FULL && selectedMetadata.t.toLowerCase() == TYPES.PERMISSION_SET ))),
        delete: commitedMetadata.all.filter(selectedMetadata => selectedMetadata.a.toLowerCase() == ACTIONS.DELETE),
        profiles: commitedMetadata.all.filter(selectedMetadata => selectedMetadata.a.toLowerCase() == ACTIONS.FULL && selectedMetadata.t.toLowerCase() == TYPES.PROFILE),
        getMetadataList(type) {
            return this[type].map(step => step.t + ':' + step.n).toString(',');
        }
    }
    if(commitedMetadata?.profiles?.length) {
        commitedMetadata.fullProfileList = commitedMetadata?.profiles?.map(profile => profile.n).toString(',');
    }
}

function getPromotionAndConfigureGit() {
    const {
        git_email,
        git_name
    } = env;

        executeCommand(`
            sfdx -v
            # Get Promotion
            mkdir -p ${targetDirectory}
            cd ${targetDirectory}
            copado --progress "fetching ${promotion}"
            copado-git-get "${promotion}" || ${getErrorCmdString('Error fetching promotion branch')}

            # Configure Git
            git config --local user.email "${git_email}" || exit 1
            git config --local user.name "${git_name}" || exit 1
            git config --global diff.renames false || exit 1
            git config --global merge.renames false || exit 1
            git config --global status.renames false || exit 1
        `, STDIO.INHERIT);
        chdir(targetDirectory);
}

function checkSFDXProjectJson() {
    const sfdxProjectJsonPath = `${targetDirectory}/sfdx-project.json`;
    if(existsSync(sfdxProjectJsonPath)) {
        let fileContent = JSON.parse(readFileSync(sfdxProjectJsonPath, { encoding:'utf8' }));
        if(fileContent.sourceApiVersion !== sourceApiVersion) {
            const commitMessage = `Updated  sourceApiVersion from ${fileContent.sourceApiVersion} to ${sourceApiVersion} in sfdx-project.json to align the commit, promote and deploy operations with the latest supported api version of Copado.`;
            fileContent.sourceApiVersion = sourceApiVersion;
            writeFileSync(sfdxProjectJsonPath, JSON.stringify(fileContent, null, 2));
            commitGit(commitMessage); 
        }
    } else {
        throw "Invalid configuration. sfdx-project.json is invalid or missing at project root. Copado Commit and Deploy operations are required to run from within a valid sfdx project.";
    }
}

function enrichChangeList(filePath) {
    executeCommand(`
    copado -p "Processing metadata"
    enricher -p ${filePath} --repo ${targetDirectory}/ || ${getErrorCmdString("Error finding file paths for the deployed changes")}`);
}

function enrichChangeListAndProcessMetadata() {
    enrichChangeList(`/app/${encodedChangesFile}`);

    executeCommand(`
    metadata-processor "/app/${encodedChangesFile}" "${targetDirectory}" -o TRIM || ${getErrorCmdString("Error processing metadata")}`, STDIO.INHERIT);
    if(existsSync(`/app/internal_${encodedChangesFile}`)) {
        addParentMetadataToOriginalChangeList(require(`/app/internal_${encodedChangesFile}`));
    }
}
function addParentMetadataToOriginalChangeList(parentMetadataList) {
    commitedMetadata.add = commitedMetadata.add.concat(parentMetadataList.filter(change => change.a.toLowerCase() == ACTIONS.ADD));
}


function varReplace() {
    const filesToIncludeListPath = '/tmp/filesToInclude.json',
        { destination_env } = env;

    writeFileSync(filesToIncludeListPath, JSON.stringify(getFilePaths(`/app/${encodedChangesFile}`, [ ACTIONS.ADD, ACTIONS.FULL ] )));

    const varreplace = `
    copado -p "Replacing environment variables, if any"
    varreplace '${destination_env}' '${targetDirectory}' --include=${filesToIncludeListPath} || ${getErrorCmdString("Error replacing environment variables")}`;

    executeCommand(varreplace, STDIO.INHERIT);
}

function replaceEnvironmentVariablesAndReplaceYAML() {
    const {
        find_and_replace_rules
    } = env;

    if(commitedMetadata.add?.length > 0 || commitedMetadata.profiles?.length > 0) {
        varReplace();

        executeCommand(`
            copado -p "Applying global find and replace rules, if any"
            if [ -z "${find_and_replace_rules}" ]
            then
                echo "No yaml file found"
            else
                copado --downloadfiles "${find_and_replace_rules}" --downloaddir /tmp/ || ${getErrorCmdString("Error downloading Copado.yml file")}

                # YAML Replace service called, Target Dir:  "/tmp/Copado", Path to YML: "/app/repository/"
                yamlreplace "/tmp/Copado" "${targetDirectory}" -b "${promotion}" ||  ${getErrorCmdString("Error applying global find and replace rules")}
            fi
        `, STDIO.INHERIT);
    }  
}

function copadoStartDeployment() {

    const addManifest = commitedMetadata.profiles?.concat(commitedMetadata.add).filter(meta => meta != undefined);
    let manifestCreation = `
        copado --progress 'Deploying ${commitedMetadata.getMetadataList(ACTIONS.ADD).substring(0,240)}...'
        sfdx force:source:manifest:create -m '${addManifest.length ? addManifest.map(step => step.t + ":" + step.n).toString() : ""}'
    `;
    commitedMetadata.delete.length && (manifestCreation += `\nsfdx force:source:manifest:create -m '${commitedMetadata.getMetadataList('delete')}' --manifesttype destroy`);

    executeCommand(manifestCreation, STDIO.INHERIT);
    return executeSFDXcommand(`(${createSfdxDeploy()}) || true`);

}

function executeSFDXcommand(command) {
    return JSON.parse(executeCommand(command));
}


// Create SFDX Deploy command w.r.t. to flags
function createSfdxDeploy() {
    const { waitTime } = env;
    let sfdxDeployCommand = `sfdx force:source:deploy --json ${ waitTime ? '-w ' + parseInt(waitTime) : ''}`;

    if(isValidation === 'true') {
        sfdxDeployCommand += " --checkonly";
    }
    sfdxDeployCommand += ` --targetusername ${destinationSessionid}`;

    sfdxDeployCommand += getTestRunParam();

    sfdxDeployCommand += " --manifest package.xml" + (commitedMetadata.delete.length ? " --postdestructivechanges destructiveChanges.xml -g" : "");
    console.log(`Deploy command: ${sfdxDeployCommand}`);
    return `${sfdxDeployCommand} || true`;
}
function getTestRunParam() {
    let testRunParam = '';
    const { testLevel } = env;
    if(testLevel) {
        const testRun = testLevel?.split(' ')?.join('');
        if (testRun) {
            testRunParam += ` -l ${testRun}`;
            if(testRun === "RunSpecifiedTests") {
                testRunParam += `${ " -r " + getDeploymentTestClasses()}`
            }
        }
    }
    return testRunParam;
}

function getDeploymentTestClasses() {
   const { testOnlyClasses, testClasses } = env;
   let deploymentTestClasses = new Set();
    if(testOnlyClasses) {
        deploymentTestClasses = new Set([...deploymentTestClasses, ...(testOnlyClasses?.split(','))]);
    }
    if(testClasses) {
        deploymentTestClasses = new Set([...deploymentTestClasses, ...(testClasses?.split(','))]);
    }
    if(!deploymentTestClasses.size) {
        throw "No Test Classes were selected by the User";
    }
    return [...deploymentTestClasses].join(',');
}

function uploadDeploymentResultOnJobStep(deployResult) {
        deployResultDir = `/app/${isValidation === "true" ? 'Validation':'Deployment'}Result.json`;

    writeFileSync(deployResultDir, JSON.stringify(deployResult, null, 2));
    executeCommand(`copado --uploadfile ${deployResultDir}`, STDIO.INHERIT);
}

function handleDeployResult(deployResult) {

    uploadDeploymentResultOnJobStep(deployResult);

    let errorResponse = '';
    const deployResultDetails = deployResult?.result?.details,
       failedMetadata = deployResultDetails?.componentFailures,
       warningSuccessMetadata = deployResultDetails?.componentSuccesses,
       failedTests = deployResultDetails?.runTestResult?.failures,
       codeCoverageWarnings = deployResultDetails?.runTestResult?.codeCoverageWarnings,
       filterFailedMedataProblemType = (statusInfo, type) => {
            let result = [];
            if(Array.isArray(statusInfo)) {
                result = statusInfo.filter(status => status.problemType == type);
            } else if(statusInfo.problemType == type) {
                result = [statusInfo];
            }
            return result;
       },
       getFailedMedataErrorMessage = (failedTests) => {
            let result = '';
            if(Array.isArray(failedTests)) {
                let failedTestsError = failedTests.map(fail => {
                    let mappedValue = "";
                    if(fail.name) {
                        mappedValue = mappedValue.concat(fail.name);
                    }
                    if(fail.methodName) {
                        mappedValue =  mappedValue.concat(`${fail.methodName}-`);
                    }
                    mappedValue = mappedValue.concat(fail.message);
                    return mappedValue;
                });
                if(failedTestsError?.length) {
                    result = failedTestsError.join("\n");
                }
            }
            else {
                if(typeof failedTests.name != 'object') {
                    result = result.concat(`${failedTests.name}-`);
                }
                if(failedTests.methodName) {
                    result = result.concat(failedTests.methodName);
                }
                result = result.concat(failedTests.message);
            }
            return result;
       },
       getFailedDeploymentMessage = (problem) => {
            let result = '';
            if(Array.isArray(problem)) {
                result = problem.map(fail => fail['componentType'].concat(':', fail['fullName'], ':', fail['problem'])).join("\n");
            }
            else {
                result = problem['componentType'].concat(':', problem['fullName'], ':', problem['problem']).toString();
            }
            return result;
       };

   // If any errors or warning, display to progress indicator and fail the job execution
   if (deployResult?.status) {
        if(deployResult?.message) {
            errorResponse = `${deployResult?.message}`;
        }
        else if(deployResult?.result.status == 'Canceled') {
            errorResponse = `The ${ isValidation === 'true' ? 'validation' : 'deployment' } was cancelled on the target org.`;
        }
   }
   if (failedTests) {
       errorResponse = populateErrorResponse(getFailedMedataErrorMessage(failedTests), errorResponse);
      }
   if (codeCoverageWarnings) {
       errorResponse = populateErrorResponse(getFailedMedataErrorMessage(codeCoverageWarnings), errorResponse);
   }
   if (failedMetadata) {
       const errorMetadata = filterFailedMedataProblemType(failedMetadata, "Error");
       if(errorMetadata.length){
        errorResponse = populateErrorResponse(getFailedDeploymentMessage(errorMetadata), errorResponse);
       }
   }
   if(warningSuccessMetadata?.length) {
       const warningMetadata = filterFailedMedataProblemType(warningSuccessMetadata, "Warning");
       if(warningMetadata?.length) {
           message = `WARNING deploying Metadata: ${getFailedDeploymentMessage(warningMetadata)}`;
           executeCommand(`copado -p "${message.substring(0,254)}"`);
           console.log(message);
       }
   }
   if(errorResponse) {
        executeCommand(getErrorCmdString(errorResponse.substring(0,131072)));
   }

   if(!deployResult.status && isValidation === "true") {
       populateValidationIdOnResultRecord(deployResult.result.id);
   }
}

function populateErrorResponse(errorMessage, errorResponse) {
    const delimeter = "\n";
    errorResponse = (errorResponse && errorResponse?.trim().concat(`${delimeter}`, errorMessage.trim())) || errorResponse.concat(errorMessage.trim());
    return errorResponse;
}

function cleanSfdxProjectConfigFiles() {
    const cleanUp = `
        git reset --hard
        rm -rf package.xml destructiveChanges.xml
    `;
    executeCommand(cleanUp);
}

function gitMergePromotionToTarget() {
    const { git_depth } = env;

    let readyToMerge = `
        git reset --hard || exit 1
        copado --progress "merging ${promotion} into ${target_branch}"
        copado-git-get --depth ${git_depth} "${target_branch}" ||  ${getErrorCmdString('Error fetching target branch')}
        git merge "${promotion}"  --no-commit -Xignore-space-change || true
    `;
    executeCommand(readyToMerge, STDIO.INHERIT);

    evaluateMergeStatus();
    commitGit(`Merging ${promotion} into ${target_branch} after auto conflict resolution`);
}

function commitGit(commitMessage) {
    executeCommand(`           
        git add . # add all the resolved changes, if any 
        git commit -am "${commitMessage}" || true`, STDIO.INHERIT);
}

function evaluateMergeStatus() {
        gitStatus = executeCommand('git status --porcelain=v1 -uno'),
        porcelainStatus = gitStatus.split('\n').map(str => str.split(' ')?.[0]),
        isConflict = porcelainStatus.length ? ['AA', 'UU', 'DD', 'UA', 'UD', 'DU', 'AU'].some(s => porcelainStatus.includes(s)) : false;
    let infoMessage;

    if(isConflict)
        executeCommand(getErrorCmdString(`Changes detected in target branch '${target_branch}' after promotion branch '${promotion}' was created, please recreate promotion branch out of the new target branch state.`));
    else if(!gitStatus) {
        infoMessage = 'Already up to date';
    }
    else {
        infoMessage = `Changes detected in target branch '${target_branch}' after promotion branch '${promotion}' was created.`;

    }
    infoMessage && executeCommand(`copado -p "${infoMessage}"`);
    console.log(infoMessage);
}

function reconcileFullProfile() {
    if (commitedMetadata?.profiles?.length) {
        let reconciledProfileOutput = executeCommand(`
                copado -p "Reconciling full profile"
                sfdx sfpowerkit:source:profile:reconcile -u "${destinationSessionid}" -n "${commitedMetadata?.fullProfileList}" --json || true
        `);
        if (!reconciledProfileOutput) {
            throw "Error reconciling full profile";
        }
        evaluateResponse(reconciledProfileOutput);
    }
}

function pushChangesToRemote(remoteBranchName) {
    executeCommand(`git push origin "${remoteBranchName}" || ${getErrorCmdString("Could not push the changes to " +remoteBranchName)}`, STDIO.INHERIT);
}

function createDebugPromotionBranch(debugBranch) {

    executeCommand(`
    exists=$(git show-ref refs/heads/${debugBranch})
    if [ -n "$exists" ]; then
        copado -p "Removing local debug branch ${debugBranch}"
        git branch -d ${debugBranch}
    fi
    git checkout -b ${debugBranch}
    `, STDIO.INHERIT);
}

function pushDebugPromotionBranch(debugBranch) {
    executeCommand(`copado -p "Pushing changes to ${debugBranch}"`);
    commitGit(`Copado Debug Branch Commit for Promotion ${debugBranch}`);
    executeCommand(`git push -f origin ${debugBranch} || copado -p "Could not push changes to ${debugBranch}"`, STDIO.INHERIT);
}

function setSFDXConfig(instanceUrl) {
    executeCommand(`
    # Set instance URL within sfdx-config.json
    sfdx config:set instanceUrl=${instanceUrl.substring(0, instanceUrl.indexOf('.com') + 4)}`);
}

function getErrorCmdString(error) {
    const suffix = 'Please check the logs for details.'
    return `{ copado -p "Error" -e "${error.trim()}; ${suffix}"; exit 1; }`
}

function populateValidationIdOnResultRecord(validationId) {
   executeCommand(`copado -p "Saving validation id" -r '${JSON.stringify({validationId})}' && exit 0`);
}

function executeQuickDeploy(validationId) {
    const quickDeployResult = executeSFDXcommand(`sfdx force:source:deploy --json -q ${validationId} -u ${destinationSessionid}|| true`);
    if(quickDeployResult.status) {
        executeCommand(`copado -p "${quickDeployResult.message}"`);
        handleDeployResult(copadoStartDeployment());
    }
    else {
        uploadDeploymentResultOnJobStep(quickDeployResult);
        executeCommand(`copado -p "Quick Deploy done successfully"`);
    }
}

function readFromPath(filePath) {
    return JSON.parse(readFileSync(filePath, 'utf-8'));
}

function getFilePaths(changeFilePath, actions) {
    let changeList = readFromPath(changeFilePath);
    let filePaths = new Set();
    if(actions?.length) {
        changeList = changeList.filter(change => actions.includes(change.a.toLowerCase()));
    };
    changeList.forEach(change => {
        if(change.j) {
            const filesToBeAdded = (JSON.parse(change.j).filePath).filter(file => existsSync(file) && statSync(file).isFile());
            filePaths = new Set([...filePaths, ...filesToBeAdded]);
          }
    });
    return [...filePaths];
}

function executeCommand(cmd, ioconfig) {
    const options = {
         shell: true,
         maxBuffer:  MAXBUFFER
    };
    if(ioconfig) {
        options.stdio = ioconfig;
    }
    const response = spawnSync(cmd, options);
    if(response?.status != 0) {
        if(response?.status == 1) {
            exit(1);
        }
        if(response?.stderr) {
            throw response?.stderr;
        }
    }
    return response?.stdout?.toString();
}

function processFullProfiles() {
    if (commitedMetadata?.profiles?.length) {
        const { sourceInstanceUrl } = env;
        setSFDXConfig(destinationInstanceUrl);
        reconcileFullProfile();
        setSFDXConfig(sourceInstanceUrl);
        if(commitedMetadata?.add?.length) {
            mergeFullProfile();
        }
        rmSync("sfpowerkit-cache.db", {
            force: true,
        });
        commitGit(`Cleaned full profile in promotion ${promotion}`);
    }
}

function mergeFullProfile() {
        const { sourceSessionid } = env;
        executeCommand(`copado -p "Merging selected metadata permissions to the profile"`, STDIO.IGNORE);
        const mergeProfile = `sfdx sfpowerkit:source:profile:merge -n "${commitedMetadata.fullProfileList}" -u ${sourceSessionid} -m '${commitedMetadata.getMetadataList(ACTIONS.ADD)}' --json || true`;
        const response = executeCommand(mergeProfile);
        if (!response) {
            throw "Error merging permissions to full profile";
        }
        evaluateResponse(response);
}

function evaluateResponse(response) {
    response = JSON.parse(response);
    if (response?.status && response?.message) {
        executeCommand(getErrorCmdString(`${response.commandName + ": " + response.message}`));
    }
}

