Records.copado__Script__c:
#!/usr/bin/env node
/**
 * Performs commit of selected user story metadata changes.
 * Returns (If ACTION success) new feature branch in user repo with all the changes and returns the commit id in the result record
 * (If ACTION failed) Returns details with error status on user story commit record
 * @param file_changes_id
 * @param file_name
 * @param source_sessionid
 * @param source_endpoint
 * @param namespace
 * @param source_path
 * @param git_json
 * @param base_branch
 * @param source_env
 * @param find_and_replace_rules
 * @param feature_branch
 * @param recreate_if_exists
 * @param commit_message
 * @param git_email
 * @param git_name
 * @param source_environment_branch
 * @param maxBuffer
*/
const { exec, spawnSync } = require('child_process'),
    { writeFileSync, readFileSync, existsSync, rmSync, statSync } = require('fs'),
    { env, chdir, exit } = require('process'),
    sourceApiVersion = "55.0",
    targetDirectory = "/app/repository",
    { feature_branch, source_sessionid, maxBuffer } = env,
    commitChangesFilePath = "/app/encoded_changes.json",
    retrieveResultPath = "/tmp/retrieveResult.json",
    ACTIONS = {
        ADD: 'add',
        RETRIEVE_ONLY: 'retrieveonly',
        FULL: 'full',
        DELETE: 'delete'
    },
    TYPES = {
        PERMISSION_SET: 'permissionset',
        PROFILE: 'profile'
    },
    STDIO = {
        INHERIT: 'inherit',
        PIPE: 'pipe',
        IGNORE: 'ignore'
    },
    PARENT_NESTED_METADATA = ["customlabels","workflow","sharingrules","escalationrules","matchingrules","autoresponserules","assignmentrules"],
    MAXBUFFER = parseInt(maxBuffer),
    resultData = {};

let commitChanges = {};

// EXECUTION

execute();

function execute() {
    try {
        encodeFileNames();
        prepareMetadataChangesList();
        checkNestedParentMetadataDeletion();
        fetchBaseBranch();
        configureGit();
        fetchCreateFeatureBranch();
        if(commitChanges.addOrRetrieve.length > 0 || commitChanges.profiles.length > 0) { 
          Promise.all(retrieveOrgMetadata()).then(() => {
          enrichChangeList(commitChangesFilePath, true);
          varReplace();
          findAndReplace();
          processMetadata();
          discardRetrieveOnlyFiles();
          commitAndUpdateEnvironmentBranches();
          }).catch((err) => {
                console.log(err.toString());
                executeCommand(`copado -p "Error" -e "${err.toString()}"`, STDIO.IGNORE);
                exit(1);
          });
        } else {
          enrichChangeList(commitChangesFilePath, false);
          processMetadata();
          commitAndUpdateEnvironmentBranches();
        }
    } catch(err) {
        console.log(err.toString());
        executeCommand(`copado -p "Error" -e "${err.toString()}"`, STDIO.IGNORE);
        exit(1);
    }
}

// SCRIPT FUNCTIONS

function encodeFileNames() {
    committedMetadata = getCommitChanges();
    for (let commitChange of committedMetadata) { 
        switch (commitChange.t) {
            case 'DashboardFolder':
            case 'ReportFolder':
            case 'Document':
                commitChange.n = commitChange.n.replace(/%2F/gi, '/');
                break;

            case 'EmailTemplate':
                commitChange.n = commitChange.n.replace(/%24/gi, '$').replace(/%2F/gi, '/');
                break;

            case 'Layout':
                const regExp = '(?:([a-zA-Z_][a-zA-Z0-9_]{0,14}?(?!__c-))__)?([^-]+)-(?:([a-zA-Z_][a-zA-Z0-9_]{0,14}?_?(?!__c$))__)?(.+)\n?';
                /*
                    The above regExp divides the layout full name into 4 groups:
                    1. customobject namespace
                    2. customobject name
                    3. layout namespace
                    4. layout mame
                    The match function returns these groups respectively in the indices 1,2,3,4
                */
               	const layoutFullName = commitChange.n.match(regExp);
                let layoutName = layoutFullName[4].replace(/_{2}(?!c)/g, (match) => {return match.replace(/_/gi,'%5F') });
                layoutName = layoutName.replace(/\./,'%2E');
                commitChange.n = `${layoutFullName[1] ? layoutFullName[1] + "__" : ""}${layoutFullName[2]}-${layoutFullName[3] ? layoutFullName[3] + "__" : ""}${layoutName}`;
                break;
        }
    }
    writeFileSync(commitChangesFilePath, JSON.stringify(committedMetadata));
    return committedMetadata;
}

function getCommitChanges() {
    const { file_name, file_changes_id } = env;
    executeCommand(`copado --downloadfiles ${file_changes_id} --downloaddir /tmp/`);
    let downloadedFileName;
    if(existsSync(`/tmp/${file_name}`)) {
        downloadedFileName = file_name;
    } else if(existsSync(`/tmp/${file_name}.json`)) {
        downloadedFileName = `${file_name}.json`;
    }
    else {
        throw 'Error fetching Commit Changes';
    }
    return readFromPath(`/tmp/${downloadedFileName}`);
}

function prepareMetadataChangesList() {
    commitChanges.all = require(commitChangesFilePath);
    commitChanges = { ...commitChanges,
      addOrRetrieve: commitChanges.all.filter(selectedMetadata => (selectedMetadata.a.toLowerCase() == ACTIONS.RETRIEVE_ONLY || selectedMetadata.a.toLowerCase() == ACTIONS.ADD || (selectedMetadata.a.toLowerCase() == ACTIONS.FULL && selectedMetadata.t.toLowerCase() == TYPES.PERMISSION_SET))),
      delete: commitChanges.all.filter(selectedMetadata => selectedMetadata.a.toLowerCase() == ACTIONS.DELETE),
      profiles: commitChanges.all.filter(selectedMetadata => selectedMetadata.a.toLowerCase() == ACTIONS.FULL && selectedMetadata.t.toLowerCase() == TYPES.PROFILE),
      retrieveOnly: commitChanges.all.filter(selectedMetadata => selectedMetadata.a.toLowerCase() == ACTIONS.RETRIEVE_ONLY),
      getMetadataList(type) {     
        return this[type].map(step => step.t + ':' + step.n).toString(',');
      }
    };
}

function checkNestedParentMetadataDeletion() {
    if(commitChanges?.delete?.length) {
        const nestedParentChangeList = commitChanges.delete.filter(change => PARENT_NESTED_METADATA?.includes(change.t.toLowerCase())).map(change => change.t);
        if(nestedParentChangeList?.length) {
            throw `${nestedParentChangeList.toString()} cannot be selected for a destructive commit. Try re-committing after removing these metadata`;
        }
    }
}

function setup() {
    checkSFDXProjectJson(feature_branch);
    configureSFDXCLI();
}

function checkSFDXProjectJson(branchName) {
    const sfdxProjectJsonPath = `${targetDirectory}/sfdx-project.json`;
    if(existsSync(sfdxProjectJsonPath)) {
        let fileContent = readFromPath(sfdxProjectJsonPath);
        if(fileContent.sourceApiVersion !== sourceApiVersion) {
            const commitMessage = `Updated  sourceApiVersion from ${fileContent.sourceApiVersion} to ${sourceApiVersion} in sfdx-project.json to align the commit, promote and deploy operations with the latest supported api version of Copado`;
            fileContent.sourceApiVersion = sourceApiVersion;
            writeFileSync(sfdxProjectJsonPath, JSON.stringify(fileContent, null, 2));
            gitCommit(commitMessage, branchName); 
        }
    } else {
        throw `Invalid configuration in ${branchName}. sfdx-project.json is invalid or missing at project root. Copado Commit and Deploy operations are required to run from within a valid sfdx project.`;
    }
}

function configureSFDXCLI() {
    const { source_endpoint } = env;
    const baseUrl = source_endpoint.substring(0, source_endpoint.indexOf('/', source_endpoint.indexOf('/') + 2));

    const configSet = `
    sfdx config:set instanceUrl=${baseUrl}
    sfdx config:set apiVersion=${sourceApiVersion}`;
    executeCommand(configSet);
}

function sfdxRetrieve(resolve, reject) {

    const retrieve = `
        sfdx force:source:retrieve --json -u "${source_sessionid}" -m '${commitChanges.getMetadataList('addOrRetrieve')}' || true
    `;
    exec(retrieve, { stdio: STDIO.INHERIT, maxBuffer: MAXBUFFER }, (err, retrieveResult, stderr) => {
        // Adding the retrive result to the logs
        retrieveResult = JSON.parse(retrieveResult.toString());

        if(retrieveResult.status  && retrieveResult.message) {
           reject(retrieveResult.message);
        }

        const failedMetadataList = retrieveResult?.result?.inboundFiles?.filter(file => file.state == 'Failed');
        if(failedMetadataList?.length) 
        { 
            const err = failedMetadataList.map(metadata => metadata.error).toString();
            reject(err);
        }
        writeFileSync(retrieveResultPath, JSON.stringify(retrieveResult));
        executeCommand(`copado --uploadfile ${retrieveResultPath}`);
        resolve(retrieveResult);
    });
}

function retrieveFullProfile(resolve, reject) {

        const retrieveFullProfile = `
        sfdx sfpowerkit:source:profile:retrieve -u "${source_sessionid}" -n "${commitChanges.profiles.map(profile => profile.n).toString()}" 
        `
        exec(retrieveFullProfile, { stdio: 'inherit' }, (err, profileResult, stderr) => {
            if(err) {
                console.log(profileResult);
                reject("Full Profile Retrieval Failed");
            } else {
                rmSync("sfpowerkit-cache.db", {
                    force: true,
                });
                resolve();
            }
        });
}
function retrieveOrgMetadata() {

    setup();
    const setProgress = `
    copado -p "Retrieving the selected metadata"`
    executeCommand(setProgress);
    const promises = [];
    commitChanges.profiles.length && promises.push( new Promise((resolve, reject) => {
        retrieveFullProfile(resolve, reject);
    }));
    commitChanges.addOrRetrieve.length && promises.push(new Promise((resolve, reject) => {
        sfdxRetrieve(resolve, reject);
    }));
    return promises;
}

function varReplace() {
    const filesToIncludeListPath = '/tmp/filesToInclude.json',
    filesToInclude = getFilesInScope();
    if(filesToInclude?.length) {
        writeFileSync(filesToIncludeListPath, JSON.stringify(filesToInclude));
        const { source_env } = env,
            varreplace = `
            copado -p "Replacing environment variables, if any"
            varreplace '${source_env}' '${targetDirectory}' --valuename=true  --include=${filesToIncludeListPath} || ${getErrorCmdString("Error replacing environment variables")}`;
            executeCommand(varreplace, STDIO.INHERIT);
    }
}

function getFilesInScope() {
    let filesInScope = [];
    if(existsSync(retrieveResultPath)) {
        const retrieveResult = readFromPath(retrieveResultPath);
        filesInScope = retrieveResult?.result?.inboundFiles?.map(file => file.filePath.substring(targetDirectory.length + 1));
    };
    const retrieveOnlyFilePaths = getFilePaths(commitChangesFilePath, [ACTIONS.RETRIEVE_ONLY]);
    if(retrieveOnlyFilePaths?.length && filesInScope?.length) {
        filesInScope = filesInScope.filter(file =>  !(retrieveOnlyFilePaths.includes(file)));
    }
    return filesInScope;
}

function fetchBaseBranch() {
    const { base_branch, git_depth } = env,
        fetchBaseBranch = `
    mkdir -p ${targetDirectory} && cd ${targetDirectory}
    copado-git-get --depth "${git_depth}" "${base_branch}" || ${getErrorCmdString("Error fetching " + base_branch)}
    `;
    executeCommand(fetchBaseBranch);
    chdir(`${targetDirectory}`);
}

function fetchCreateFeatureBranch() {
    const { recreate_if_exists } = env;
    const fetchCreateFeatureBranch = `
    if [ -n "${feature_branch}" ]; then
        if [ "${recreate_if_exists}" == "true" ]; then
        copado -p "Removing branch ${feature_branch}"
        git branch -D "${feature_branch}" || true
        git push origin --delete "${feature_branch}" || true
        fi
        copado -p "Fetching/creating branch ${feature_branch}"
        copado-git-get -c "${feature_branch}" ||  ${getErrorCmdString("Error fetching/creating " + feature_branch)}
    fi
    `
  executeCommand(fetchCreateFeatureBranch, STDIO.INHERIT);
}

function findAndReplace() {
    const { find_and_replace_rules } = env,
        pathToYaml = "/tmp/Copado";
    if(find_and_replace_rules) {
        const findAndReplace = `
        copado -p "Applying global find and replace rules, if any"
        copado --downloadfiles "${find_and_replace_rules}" --downloaddir /tmp/
        yamlreplace "${pathToYaml}" "${targetDirectory}" -b "${feature_branch}" ||  ${getErrorCmdString("Error applying find and replace rules")}` 
        executeCommand(findAndReplace, STDIO.INHERIT);
    }
}

function enrichChangeList(filePath, includeCliResponse) {
    executeCommand(`enricher -p ${filePath} --repo ${targetDirectory}/ ${ includeCliResponse ? ( existsSync(retrieveResultPath) ? '--cliresponse ' + retrieveResultPath : '') : ' '} || ${getErrorCmdString("Error finding file paths for the committed changes")}`, STDIO.INHERIT);
}

function processMetadata() {
    const pocesssMetadata = `
            copado -p "Processing metadata"
            metadata-processor "${commitChangesFilePath}" "${targetDirectory}" -o COMMIT ||  ${getErrorCmdString("Error processing metadata")}
        `;
    executeCommand(pocesssMetadata, STDIO.INHERIT);
}

function gitCommit(commitMessage, branchName) {
    const  gitCommit = `
    copado -p "Committing ${commitMessage} in ${branchName}"
    git add .
    git status
    git commit -m "${commitMessage}" || ${getErrorCmdString("There was some issue when committing metadata")}
    `;
    executeCommand(gitCommit, STDIO.INHERIT);
}

function configureGit () {
    const { git_email,  git_name } = env,
    configureGit = `
        git config --local user.email "${git_email}" || exit 1
        git config --local user.name "${git_name}" || exit 1
        git config --global diff.renames false || exit 1
        git config --global merge.renames false || exit 1
        git config --global status.renames false || exit 1
        `;
    executeCommand(`${configureGit}`, STDIO.IGNORE);
}

function commitAndUpdateEnvironmentBranches() {
    const { commit_message, source_environment_branch} = env,
    gitStatus = executeCommand(`
    git status --porcelain`);
    if(!gitStatus) {
        resultData.status = "No Changes";
        executeCommand(`copado -p "There are no changes to be committed" -r '${JSON.stringify(resultData)}' && exit 0`);
    }
    else {
        const fetchSourceBranch = `
        copado -p "Fetching source branch - ${source_environment_branch}"
        copado-git-get "${source_environment_branch}" || ${getErrorCmdString("Error fetching " + source_environment_branch)}
        `;

        const mergeFeatureBranchInSource = `
        copado -p "Merging changes to source branch - ${source_environment_branch}"

        # We have used || true with git merge because:
        # 1. We do not want this command to fail. After merge, if there is a conflict, we only want to find the git status after the merge and send it to the copado-merge service and they will handle the conflicts for us.
        # 2. If we fail this command on a conflict, then we will not be able to send control to the copado-merge service.
        git merge "${feature_branch}" -Xignore-space-change || true
        copado -p "Resolving git conflicts, if any"
        git status --porcelain=v1
        git status --porcelain=v1 > /tmp/git_status.txt
        echo "git_status"
        cat /tmp/git_status.txt
        touch /tmp/output.json
        copado-merge ${targetDirectory} ${commitChangesFilePath} -p /tmp/git_status.txt -t sfdx -c --out /tmp ||  ${getErrorCmdString("There was some issue merging " + feature_branch + " into " + source_environment_branch)}
        git add . # add all the resolved changes, if any
        git commit -am "Merging ${feature_branch} into ${source_environment_branch} after auto conflict resolution" || true
        `;

        gitCommit(commit_message, feature_branch);
        gitPush(feature_branch);

        saveCommitDetails();

        executeCommand(fetchSourceBranch, STDIO.INHERIT);

        executeCommand(mergeFeatureBranchInSource, STDIO.INHERIT);

        checkSFDXProjectJson(source_environment_branch);
        gitPush(source_environment_branch);
    }
}

function getCommitId() {
    return executeCommand(`git rev-parse HEAD`)?.trim();
}

function saveCommitDetails() {
    resultData.commitId = getCommitId();
    executeCommand(`copado -p "Saving commit details" --result-data '${JSON.stringify(resultData)}'  || ${getErrorCmdString('Error saving commit details')}`);
}

function gitPush(branchName) {
    const gitPush = `
    copado -p "Pushing all changes to ${branchName}"
    git push origin "${branchName}" ||  ${getErrorCmdString("Could not push the changes")}
    `;
    executeCommand(gitPush);
}

function getErrorCmdString(error) {
    const suffix = 'Please check the logs for details.'
    return `{ copado -p "Error" -e "${error}. ${suffix}"; exit 1; }`
}

function readFromPath(filePath) {
    return JSON.parse(readFileSync(filePath, 'utf-8'));
}

function getFilePaths(changeFilePath, actions) {
    let changeList = readFromPath(changeFilePath);
    let filePaths = new Set();
    if(actions?.length) {
        changeList = changeList.filter(change => actions.includes(change.a.toLowerCase()));
    };
    changeList.forEach(change => {
        if(change.j && change.j !== '') {
            const filesToBeAdded = (JSON.parse(change.j)?.filePath)?.filter(file => existsSync(file) && statSync(file).isFile());
            if(filesToBeAdded) {
                filePaths = new Set([...filePaths, ...filesToBeAdded]);
            }
          }
    });
    return [...filePaths];
}

function discardRetrieveOnlyFiles() {
    if(commitChanges.retrieveOnly?.length) {
        const retrieveChangesPath = '/tmp/retrieveOnlyFiles.json';
        writeFileSync(retrieveChangesPath, JSON.stringify(commitChanges.retrieveOnly));
        enrichChangeList(retrieveChangesPath, false);
        discardFilesInGit(getFilePaths(retrieveChangesPath));
    }
}

function getUntrackedFiles() {
    return executeCommand(`git ls-files --others --exclude-standard`).split('\n');
}

function executeCommandinChunks(fileList, cmd) {
    const { chunkSize } = env;
    if(fileList.length > 0) {
        let fileIndex = 0;
        do {
            let endIndex = fileIndex + parseInt(chunkSize) > fileList.length ? fileList.length : fileIndex + parseInt(chunkSize);
            let fileChunk = fileList.slice(fileIndex, endIndex);
            executeCommand(cmd.replace(/REPLACE_VALUE/g, fileChunk.map(file => `'${file}'`).join(' ')));
            fileIndex =  endIndex;
        } while(fileIndex < fileList.length);
    }
}

function discardFilesInGit(fileList) {
    const addedFiles = getFilePaths(commitChangesFilePath, [ ACTIONS.ADD ]);
    if(addedFiles?.length) {
        fileList = fileList.filter(file =>  !(addedFiles.includes(file)));
    }
    const unTrackedFiles = getUntrackedFiles();
    const toBeCleanedFiles = fileList.filter(file => unTrackedFiles.includes(file));
    const trackedFiles = fileList.filter(file => !(toBeCleanedFiles.includes(file)));
    executeCommandinChunks(toBeCleanedFiles, `git clean -fd REPLACE_VALUE`);
    executeCommandinChunks(trackedFiles, `( git checkout HEAD -- REPLACE_VALUE ) || true`);
}

function executeCommand(cmd, ioconfig) {
   const options = {
        shell: true,
        maxBuffer:  MAXBUFFER
   };
   if(ioconfig) {
    options.stdio = ioconfig;
   }
    const response = spawnSync(cmd, options);
    if(response?.status != 0) {
        if(response?.status == 1) {
            exit(1);
        }
        if(response?.stderr) {
            throw response?.stderr;
        }
    }
    return response?.stdout?.toString();
}

