Records.copado__Script__c:
#!/usr/bin/env node



/**
 * Performs promotion of selected user story metadata changes.
 * Returns (If ACTION success) new peomotion branch in user repo with all the changes
 * (If ACTION failed) Returns details with conflict or error status on promotion record
 * @param user_stories
 * @param promotion_branch
 * @param target_branch
 * @param tag
 * @param recreatePromotionBranch
 * @param promotion_id
 * @param file_changes_id
 * @param conflict_resolution_attachments
 * @param git_name
 * @param git_email
 * @param repository_id
 * @param git_depth
 * @param maxBuffer
*/

const { spawnSync } = require('child_process'),
    { existsSync, readFileSync, writeFileSync } = require('fs'),
    { exit, env, chdir } = require('process');

const {
    file_changes_id,
    target_branch,
    promotion_branch,
    git_depth,
    git_email,
    git_name,
    promotion_id,
    repository_id,
    conflict_resolution_attachments,
    user_stories,
    tag,
    recreatePromotionBranch,
    maxBuffer,
    file_name
} = env,
MAXBUFFER = parseInt(maxBuffer),
STDIO = {
    INHERIT: 'inherit',
    PIPE: 'pipe'
},
targetDirectory = '/app/repository',
sourceApiVersion = '55.0',
outputJSONPath = '/tmp/output.json';
let promotionChangesFilePath,
enrichedFilePath = '/tmp/enrichedPromotionChanges.json';

// EXECUTION

execute();

function execute() {

    try {
        getPromotionChanges();
        fetchTargetBranch();
        configureGit();
        fetchCreatePromotionBranch();
        checkSFDXProjectJson();
        promote();
        gitPush();
    } catch (err) {
        executeCommand(`copado -p 'Error' -e "${err.toString()}"`);
        exit(1);
    }
}

// SCRIPTS

function getPromotionChanges() {
    executeCommand(`copado --downloadfiles ${file_changes_id} --downloaddir /tmp/`);
    if(existsSync(`/tmp/${file_name}`)) {
        promotionChangesFilePath =  `/tmp/${file_name}`;
    } else if(existsSync(`/tmp/${file_name}.json`)) {
        promotionChangesFilePath = `/tmp/${file_name}.json`;
    }
    else {
        throw 'Error fetching Promotion Changes';
    }
}

function fetchTargetBranch() {

    let dirSetupCommand = `
        rm -rf ${targetDirectory}
        mkdir ${targetDirectory}
    `
    executeCommand(dirSetupCommand);
    chdir(targetDirectory);

    let command = `
    copado-git-get --depth "${git_depth ? git_depth : 100}" "${target_branch}" || ${getErrorStringAndExit("Error fetching branch " + target_branch)}`;
    executeCommand(command);
}

function fetchCreatePromotionBranch() {

    let command = `
        if [ -n "${promotion_branch}" ]; then
            if [ ${recreatePromotionBranch} == "true" ]; then
            exists=$(git ls-remote --heads origin ${promotion_branch} | wc -l)
                if [ -n "$exists" ]; then
                    copado -p "Removing branch ${promotion_branch}"
                    git push origin --delete "${promotion_branch}" || true
                fi
            fi
            copado -p "Fetching/creating branch ${promotion_branch}"
            copado-git-get -c "${promotion_branch}" ||  ${getErrorStringAndExit("Error fetching/creating branch" + promotion_branch)}
        fi
    `;
    executeCommand(command);
}
function configureGit(){
    executeCommand(`
        git config --local user.email "${git_email}" || exit 1
        git config --local user.name "${git_name}" || exit 1
        git config --global diff.renames false || exit 1
        git config --global merge.renames false || exit 1
        git config --global status.renames false || exit 1
    `);
}

function promote() {

    let gitConflictsResolution = [];
    for(let story of JSON.parse(user_stories)) {
        if(story.indexOf('/') === -1) {
            throw `User Story branch - ${story} does not follow the Copado Branching strategy`;
         }
        let userStoryName = story.split('/')[1];
        const fetchStory = `copado -p "Merging ${story} into ${promotion_branch}"
        copado-git-get --depth "${git_depth ? git_depth : 100}" "${story}" ||  ${getErrorStringAndExit("Error fetching branch " + story)}`;

        executeCommand(fetchStory);

        const enrichMetadataList = `enricher -p '${promotionChangesFilePath}' --repo ${targetDirectory}/ -u ${userStoryName}  --out ${enrichedFilePath} || ${getErrorStringAndExit("Error finding file paths for the promoted changes")}`;
        executeCommand(enrichMetadataList);

        const mergeFeatureBranchInPromotionBranch = `
            git checkout "${promotion_branch}" ||  ${getErrorStringAndExit("Error fetching branch " + promotion_branch)}
            git merge -m "Merging ${story} to ${promotion_branch}" "${story}" -Xignore-space-change || true
            copado -p "Resolving git conflicts, if any"
            git status --porcelain=v1 > /tmp/git_status.txt
            `;

        executeCommand(mergeFeatureBranchInPromotionBranch);
        let output;
        if(hasConflict()) {
            resolveConflict(userStoryName);
            if(existsSync(outputJSONPath)) {
                output = JSON.parse(readFileSync(outputJSONPath, 'utf8'));
                if(output?.some(file =>  file.strategy === "ONLINE_CONFLICT_RESOLUTION")) {
                    executeCommand(`copado -p "Conflict found while merging ${story} to $promotion_branch. Conflict needs to be resolved manually." -r '{"status": "conflicts"}'
                        exit 1`);
                }
            }
        }
        gitCommit(`Merging ${story} to ${promotion_branch} after auto conflict resolution`);
        if(output?.length) {
            gitConflictsResolution.push(...(saveMergeCommitId(output)));
        }
    }
    uploadGitConflictsResolution(gitConflictsResolution);
}
function uploadGitConflictsResolution(gitConflictsResolution){
    if(gitConflictsResolution?.length) {
        const conflictResolutionsFilePath = '/tmp/GitConflictsResolution.json';
        writeFileSync(conflictResolutionsFilePath, JSON.stringify(gitConflictsResolution, null, 2));
        executeCommand(`copado --uploadfile ${conflictResolutionsFilePath}`);
    }
}
function hasConflict() {
    const options = {
        shell: true,
        stdio: STDIO.INHERIT
    }
    return spawnSync(`${getCopadoMergeCommand(enrichedFilePath)}  check-conflict`, options)?.status;
}

function getCopadoMergeCommand(filePath) {
    return `copado-merge ${targetDirectory} '${filePath}' -p /tmp/git_status.txt -t sfdx -cp ${promotion_id} -rp ${repository_id}`;
}


function resolveConflict(userStoryName) {
    executeCommand(`
    echo "${JSON.stringify(conflict_resolution_attachments)}" > /tmp/solvedByUser.json
    touch ${outputJSONPath}`);

    executeCommand(`${getCopadoMergeCommand(enrichedFilePath)} -r /tmp/solvedByUser.json  -u ${userStoryName} --out /tmp ||  ${getErrorStringAndExit("Error processing merge")}`);
}

function gitCommit(commitMessage) {
    executeCommand(`           
        git add . # add all the resolved changes, if any 
        git commit -am "${commitMessage}" || true`);
}

function saveMergeCommitId(output) {
    const mergeCommitId = executeCommand(`git rev-parse HEAD`, STDIO.PIPE)?.trim();
    output.map(file => file.sucessfulPromotion = mergeCommitId);
    return output;
}

function gitPush(){
    executeCommand(`
        copado -p "Pushing all changes"        
        if [ -n "${tag}" ]; then
            git tag "${tag}"
            git push --all || ${getErrorStringAndExit("Could not push the changes")}
        else
            echo "No tag specified"
            git push origin "${promotion_branch}" || ${getErrorStringAndExit("Could not push the changes")}
        fi
    `);
}

function checkSFDXProjectJson() {
    const sfdxProjectJsonPath = `${targetDirectory}/sfdx-project.json`;
    if(existsSync(sfdxProjectJsonPath)) {
        let fileContent = JSON.parse(readFileSync(sfdxProjectJsonPath, { encoding:'utf8' }));
        if(fileContent.sourceApiVersion !== sourceApiVersion) {
            const commitMessage = `Updated  sourceApiVersion from ${fileContent.sourceApiVersion} to ${sourceApiVersion} in sfdx-project.json to align the commit, promote and deploy operations with the latest supported api version of Copado.`;
            fileContent.sourceApiVersion = sourceApiVersion;
            writeFileSync(sfdxProjectJsonPath, JSON.stringify(fileContent, null, 2));
            gitCommit(commitMessage);
        }
    } else {
        throw "Invalid configuration. sfdx-project.json is invalid or missing at project root. Copado Commit and Deploy operations are required to run from within a valid sfdx project.";
    }
}

function getErrorStringAndExit(err) {
    const suffix = "Please check the logs for details."
    return  `{ copado -p "Error" -e "${err}. ${suffix}"; exit 1; }`;
}

function executeCommand(cmd, ioconfig) {
    const options = {
         shell: true,
         maxBuffer:  MAXBUFFER
    };
    if(ioconfig) {
        options.stdio = ioconfig;
    }
    else {
        options.stdio = STDIO.INHERIT;
    }
    const response = spawnSync(cmd, options);
    if(response?.status != 0) {
        if(response?.status == 1) {
            exit(1);
        }
        if(response?.stderr) {
            throw response?.stderr;
        }
    }
    return response?.stdout?.toString();
}

#!/usr/bin/env node
const execSync = require('child_process').execSync,
    { existsSync, writeFileSync, readFileSync } = require('fs');

let deploymentMetadata = [];

// EXECUTION

execute();

function execute() {
    getDeployChanges();
    encodeFileNames();
}  

// SCRIPT FUNCTIONS

function getDeployChanges() {
    const { file_name, file_changes_id } = process.env;
    execSync(`copado --downloadfiles ${file_changes_id} --downloaddir /tmp/`);
    let downloadedFileName;
    if(existsSync(`/tmp/${file_name}`)) {
        downloadedFileName = file_name;
    } else if(existsSync(`/tmp/${file_name}.json`)) {
        downloadedFileName = `${file_name}.json`;
    }
    else {
        throw 'Error fetching Deployment Changes';
    }
    deploymentMetadata = readFromPath(`/tmp/${downloadedFileName}`);
}
function encodeFileNames() {
    console.log(deploymentMetadata);
    for (let change of deploymentMetadata) { 
        switch (change.t) {
            case 'DashboardFolder':
            case 'ReportFolder':
            case 'Document':
                change.n = change.n.replace(/%2F/gi, '/');
                break;

            case 'EmailTemplate':
                change.n = change.n.replace(/%24/gi, '$').replace(/%2F/gi, '/');
                break;

            case 'Layout':
                const regExp = '(?:([a-zA-Z_][a-zA-Z0-9_]{0,14}?(?!__c-))__)?([^-]+)-(?:([a-zA-Z_][a-zA-Z0-9_]{0,14}?_?(?!__c$))__)?(.+)\n?';
                const layoutFullName = change.n.match(regExp);
                let layoutName = layoutFullName[4].replace(/_{2}(?!c)/g, (match) => {return match.replace(/_/gi,'%5F') });
                layoutName = layoutName.replace(/\./,'%2E');
                change.n = `${layoutFullName[1] ? layoutFullName[1] + "__" : ""}${layoutFullName[2]}-${layoutFullName[3] ? layoutFullName[3] + "__" : ""}${layoutName}`;
                break;
        }
	}
    writeFileSync('/app/encoded_changes.json', JSON.stringify(deploymentMetadata));
}

function readFromPath(filePath) {
    return JSON.parse(readFileSync(filePath, 'utf-8'));
}

#!/usr/bin/env node

const { execSync } = require('child_process'),
    { versionDetails, BASEURL, SESSION } = process.env,
    checkLog = 'Please check the logs for details',
    { subscriberId, apiVersion } = JSON.parse(versionDetails);

// EXECUTION
try {
    BASEURL && setInstanceURL();
    createSFDXProject();
    handleVersionPromotionResponse(promotePackageVersion());

    execSync(`copado -p 'Updating Results' -r ${subscriberId}`);
} catch (err) {
    execSync(showError(err.toString()));
}

// SCRIPT FUNCTIONS
function setInstanceURL() {
    execSync(`
        copado -p 'Setting instance url'
        sfdx force:config:set instanceUrl=${BASEURL.substring(0, BASEURL.indexOf('/', BASEURL.indexOf('/') + 2))} --global || (${showError(`Error setting instance URL, ${checkLog}`)})
    `);
}
function showError(error) {
    return `copado -p 'Error' -e '${error}' && exit 1`;
  }
function createSFDXProject() {
    execSync(`
        copado -p 'Creating Temp Project'
        sfdx force:project:create -n temp || (${showError(`Error creating SFDX project, ${checkLog}`)})
    `);
}
function promotePackageVersion() {
    const promoteCmd = `sfdx force:package:version:promote -p ${subscriberId} -v ${SESSION} ${apiVersion ? `--apiversion ${apiVersion}` : ''} -n --json || true`;
    logger(`Package Version Promotion Command ==> ${promoteCmd}`);
    return JSON.parse(execSync(`
        copado -p 'Promoting package ${subscriberId}'
        cd temp
        ${promoteCmd}
        copado -p 'Package '${subscriberId}' promoted correctly'
    `).toString());
}

function handleVersionPromotionResponse(promotionResponse) {
    logger(`Package Version Promotion Response ==> ${JSON.stringify(promotionResponse)}`);
    if(promotionResponse.status) {
        execSync(showError(`${promotionResponse.message}, ${checkLog}`));
    }
}

