    console.log(text);
}

#!/usr/bin/env node
const { execSync } = require('child_process'),
	{ ENDPOINT, SESSION, installationKey, apiVersion } = process.env,
	baseUrl = ENDPOINT.substring(
		0,
		ENDPOINT.indexOf('/', ENDPOINT.indexOf('/') + 2)
	),
	checkLog = 'Please check the logs for details',
	packageVersion = JSON.parse(process.env.packageVersion),
	subscriberVersionId = packageVersion.copado__Subscriber_Version_Id__c,
	versionName = packageVersion.copado__Version_Name__c,
	description = packageVersion.copado__Version_Description__c,
	branch = packageVersion.copado__Branch__c,
	tag = packageVersion.copado__Tag__c;

// EXECUTION

try {
	setInstanceURL();
	createSFDXProject();
	handleVersionPromotionResponse(updatePackageVersion());

	execSync(
		`copado -p 'Updating result' -r '${JSON.stringify({
			packageVersion,
			installationKey,
		})}'`
	);
} catch (err) {
	execSync(showError(err.toString()));
}

// SCRIPT FUNCTIONS

function buildParameters(flags) {
	return Object.entries(flags).reduce((parameters, [key, value]) => {
		return parameters + (value ? ` --${key} '${value}'` : '');
	}, '');
}

function setInstanceURL() {
	execSync(`
        copado -p 'Setting instance url'
        sfdx force:config:set instanceUrl=${baseUrl} --global || (${showError(
		`Error setting instance URL, ${checkLog}`
	)})
    `);
}

function createSFDXProject() {
	execSync(`
        copado -p 'Creating Temp Project'
        sfdx force:project:create -n temp || (${showError(
		`Error creating SFDX project, ${checkLog}`
	)})
    `);
}

function showError(error) {
	return `copado -p 'Error' -e '${error}' && exit 1`;
}

function updatePackageVersion() {
	const parameters = buildParameters({
		targetdevhubusername: SESSION,
		package: subscriberVersionId,
		versionname: versionName,
		versiondescription: description,
		branch: branch,
		tag: tag,
		installationkey: installationKey,
		apiversion: apiVersion
	});
	const updateCmd = `
        copado -p 'Updating package version'
        cd temp
        sfdx force:package:version:update ${parameters} --json || true
    `;
	logger(`Package Version Update Command ==> sfdx force:package:version:update ${parameters} --json`);
	return JSON.parse(execSync(updateCmd).toString());
}

function handleVersionPromotionResponse(promotionResponse) {
	logger(`Package Version Update Response ==> ${JSON.stringify(promotionResponse)}`);
	if(promotionResponse.status) {
		execSync(
			showError(
				`Package Version Updated failed. ${promotionResponse.message}, ${checkLog}`
			)
		);
	}
}

function logger(text) {
    console.log(text);
}

#!/usr/bin/env node

const { mkdirSync, readFileSync } = require('fs'),
    { execSync } = require('child_process'),
    { packageId, versionName, versionNumber, description, endPoint, session, gitEmail, gitName } = process.env,
    params = JSON.parse(process?.env['jsonInformation'].replace(/(\\r\\n|\\r|\\n|\\)/g, '')),
    {
        branch,
        tag,
        loglevel,
        apiversion,
        path,
        definitionfile,
        installationkey,
        installationkeybypass,
        codecoverage,
        releasenotesurl,
        postinstallurl,
        postinstallscript,
        uninstallscript,
        skipvalidation
    } = params,
    checkLog = 'Please check the logs for details',
    baseUrl = endPoint?.substring(0, endPoint?.indexOf('/', endPoint?.indexOf('/') + 2));

// EXECUTION

execute();

function execute() {
    try {
        mkdirSync('sfProject');
        process.chdir('sfProject');

        cloneRepo();
        setInstanceURL();
        configureGit();
        updatePkgVersionCreation(pkgVersionCreation());
    } catch (err) {
        execSync(`${getErrorCmd(err.toString())}`);
    }
}

// SCRIPT FUNCTIONS
function updatePkgVersionCreation(response) {
    if (!response.status) {
        commit();
        updateTagBranchDetails(response.result.SubscriberPackageVersionId);
        setProgressStatus({
            progressStatus: 'Package version created successfully',
            resultData: `${JSON.stringify(getLatestVersion(response?.result?.SubscriberPackageVersionId))}`
        });
    } else if (response.status) {
        setProgressStatus({ progressStatus: 'Package Version creation failed', errorMessage: `Could not create package. ${response.message}` });
    }
}

function getLatestVersion(pkgVersionId) {
    const versionList = execSync(`sfdx force:package:version:list -v ${session} -p ${packageId} --verbose --json`).toString();
    return JSON.parse(versionList)?.result?.find(version => version.SubscriberPackageVersionId === pkgVersionId);
}

function pkgVersionCreation() {
    const parameters = buildParameters({
        loglevel,
        targetdevhubusername: session,
        apiversion,
        package: packageId,
        path,
        branch,
        definitionfile,
        tag,
        installationkey,
        installationkeybypass,
        wait: 1000,
        versionname: versionName,
        versionnumber: versionNumber,
        versiondescription: description,
        codecoverage,
        releasenotesurl,
        postinstallurl,
        postinstallscript,
        uninstallscript,
        skipvalidation
    });
    logger(`Package Version Create Command ==> sfdx force:package:version:create ${parameters} --json`);
    const response = execSync(`
        copado -p 'Creating package version'
        sfdx force:package:version:create ${parameters} --json || true
    `).toString();
    logger(`Package Version Create Response ==> ${response}`);
    return JSON.parse(response);
}

function createTag(tag) {
    execSync(
        `
        git tag "${tag}" HEAD || (${getErrorCmd(`Error creating tag ${tag} in git, ${checkLog}`)})
    `,
        { stdio: 'inherit' }
    );
}

function deleteTag(tag) {
    execSync(
        `
        git push --delete origin refs/tags/${tag} || (${getErrorCmd(`Error deleting existing tag ${tag} in git, ${checkLog}`)})`,
        { stdio: 'inherit' }
    );
}

function commit() {
    execSync(
        `
        git add 'sfdx-project.json' || exit 1
        git commit -m 'Committing sfdx-project.json' || exit 1
    `,
        { stdio: 'inherit' }
    );
}

function pushTagAndBranchChanges(branch, tag) {
    if (branch) {
        execSync(`git push origin ${branch} || (${getErrorCmd(`Error pushing changes in git, ${checkLog}`)})`, { stdio: 'inherit' });
    }
    if (tag) {
        execSync(`git push origin ${tag} || (${getErrorCmd(`Error pushing changes in git, ${checkLog}`)})`, { stdio: 'inherit' });
    }
}

function cloneRepo() {
    execSync(
        `
    copado -p 'Cloning git repository'
    copado-git-get ${branch} || (${getErrorCmd(`Error check out branch, ${checkLog}`)})
  `,
        { stdio: 'inherit' }
    );
}

function setInstanceURL() {
    execSync(
        `
    copado -p 'Setting instance url'
    sfdx force:config:set instanceUrl=${baseUrl} --global || (${getErrorCmd(`Error setting instance URL, ${checkLog}`)})
  `,
        { stdio: 'inherit' }
    );
}

function setProgressStatus({ progressStatus, errorMessage, resultData }) {
    execSync(`copado -p '${progressStatus}' ${errorMessage ? `-e '${errorMessage}'` : ''} ${resultData ? `-r ${JSON.stringify(resultData)}` : ''}`);
    if (errorMessage) throw errorMessage;
}

function getErrorCmd(error) {
    return `copado -p 'Error' -e '${error}' && exit 1`;
}

function readProjectJson() {
    return JSON.parse(readFileSync('sfdx-project.json', 'utf-8'));
}

function getVersionAlias(object, value) {
    return Object.keys(object).find(key => object[key] === value);
}

function updateTagBranchDetails(subscriberVersionId) {
    const gitTag = tag ? tag : getVersionAlias(readProjectJson().packageAliases, subscriberVersionId);
    let isTagExist = false;

    if (tag) {
        const remoteTags = execSync(`git ls-remote --tags --refs origin`).toString();
        isTagExist = remoteTags
            .split('\n')
            .map(step => step.substring(step.lastIndexOf('/') + 1, step.length))
            .includes(tag);
    }
    if (isTagExist) {
        deleteTag(tag);
    }
    createTag(gitTag);

    if (!tag) {
        const response = updatePackageVersionTag(session, gitTag, subscriberVersionId);
        if (response.status) {
            setProgressStatus({ progressStatus: 'Package Version tag update failed', errorMessage: response.message });
        } else {
            setProgressStatus({ progressStatus: `Package Version tag updated to ${gitTag}` });
        }
    }

    pushTagAndBranchChanges(branch, gitTag);
}

function updatePackageVersionTag(devhubSession, tag, subscriberVersionId) {
    const parameters = buildParameters({
        targetdevhubusername: devhubSession,
        package: subscriberVersionId,
        tag: tag
    });
    const updateCmd = `
        copado -p 'Updating package version'
        sfdx force:package:version:update ${parameters} --json || true
    `;
    const response = execSync(updateCmd).toString();
    logger(`Package Version Update Response ==> ${response}`);
    return JSON.parse(response);
}

function buildParameters(flags) {
    return Object.entries(flags).reduce((parameters, [key, value]) => {
        return parameters + (typeof value == 'boolean' ? (value ? ` --${key}` : '') : value ? ` --${key} "${value}"` : '');
    }, '');
}

function logger(text) {
    console.log(text);
}


function configureGit() {
    execSync(`
        git config --local user.email "${gitEmail}" || exit 1
        git config --local user.name "${gitName}" || exit 1`,
        { stdio: 'inherit' }
    );
}

#!/usr/bin/env node
const { execSync } = require('child_process'),
	{ endpoint, session, subscriberVersionId, installationKey } = process.env,
	baseUrl = endpoint.substring(0, endpoint.indexOf('/', endpoint.indexOf('/') + 2)),
	checkLog = "Please check the logs for details";

// EXECUTON

execute();

function execute() {
	try {
		setInstanceURL();
		const dependencies = getVersionDependencies();
		handleDependenciesResponse(dependencies);
	} catch (err) {
		execSync(`${getErrorCmd(err.toString())}`);
	}
}

// SCRIPT FUNCTIONS

function setInstanceURL() {
	execSync(
		`
	  copado -p 'Setting instance url'
	  sfdx force:config:set instanceUrl=${baseUrl} --global || (${getErrorCmd(
			`Error setting instance URL, ${checkLog}`
		)})
	`,
		{ stdio: "inherit" }
	);
}

function getVersionDependencies() {
	const query = `SELECT Dependencies FROM SubscriberPackageVersion WHERE Id='${subscriberVersionId}' ${addFilter()}`;
	const result = execSync(`
			copado -p 'Getting dependencies'
			sfdx force:data:soql:query -t -q "${query}" -u ${session} --json || true
		`).toString();
	return JSON.parse(result);
}

function getErrorCmd(error) {
	return `copado -p 'Error' -e "${error}" && exit 1`;
}

function addFilter() {
	return installationKey ? `AND InstallationKey='${installationKey}'` : '';
}

function handleDependenciesResponse(response) {
	let updateResult;
	if (!response.status) {
		const dependencies = response.result.records[0]?.Dependencies?.ids?.map(
			dependency => dependency.subscriberPackageVersionId
		);
		updateResult = dependencies?.length ? `copado -p 'Updating dependencies' -r '${JSON.stringify(dependencies)}'` : '';
	} else {
		updateResult = getErrorCmd(`${response.name}: ${response.message}`);
	}
	if (updateResult) {
		execSync(updateResult, {
			stdio: 'inherit'
		});
	}
}

#!/usr/bin/env node
/**
 * Performs execution of apex script using sfdx cli command.
 * @param destination_sessionid
 * @param destination_endpoint
 * @param script
 * @param destination_env_var
 * @param isValidation
 */

const fs = require("fs"),
  { execSync } = require("child_process"),
  { destination_sessionid, destination_endpoint, script, destination_env_var, isValidation } = process.env,
  destination_base_url = getBaseUrl(destination_endpoint),
  scriptName = "script.apex",
  validationModeMessage = "This step will not be executed for validate changes";

// EXECUTION

try {
  validateExecutionMode();
  setup();
  executeApexScript();
} catch (error) {
  logError(error);
}

// FUNCTIONS

function getBaseUrl(endpoint) {
  return endpoint.substring(
    0,
    endpoint.indexOf("/", endpoint.indexOf("/") + 2)
  );
}

function setup() {
  const content = script.replace("\\n", " ");
  fs.writeFileSync(scriptName, content);
  const cmd = `
      copado -p "Replacing environment dependent value"
	  varreplace '${destination_env_var}' '${scriptName}' --valuename=false
      copado -p 'Connecting to destination environment'
      sfdx force:config:set instanceUrl=${destination_base_url} --global
  `;
  execSync(cmd, { stdio: "inherit" });
}

function executeApexScript() {
  const cmd = `
        copado -p 'Executing apex script'
        sfdx force:apex:execute -u ${destination_sessionid} -f '${scriptName}' --json
  `;
  const response = execSync(cmd, { maxBuffer: 50 * 1024 * 1024 });
  validateResponse(response);
}

function validateResponse(response) {
  if (!response) {
    throw "Apex script execution failed";
  }
  const formattedResponse = JSON.parse(response.toString());
  if (formattedResponse.status != 0) {
    throw formattedResponse?.message;
  } else if (!formattedResponse?.result?.compiled || !formattedResponse?.result?.success) {
    throw `Error occured: "${JSON.stringify(formattedResponse?.result)}". Please check logs for more details.`;
  } else {
    execSync(`copado -p 'Apex script execution completed successfully'`);
  }
}

function logError(error) {
  execSync(`copado -p 'Error' -e "${error.toString()}" && exit 1`);
}

function validateExecutionMode() {
  if(isValidation === 'true') {
    execSync(`copado -p '${validationModeMessage}' --result-data '${validationModeMessage}'`);
	process.exit(0);
  }
}

#!/usr/bin/env node
const { mkdirSync, readFileSync } = require('fs'),
    { execSync } = require('child_process'),
    endPoint = process.env['ENDPOINT'],
    branchName = process.env['branch'],
    { loglevel, apiversion, packagetype, nonamespace, orgdependent, errornotificationusername, namespaceprefix } = JSON.parse(process.env['jsonInformation'].replace(/\\/g, '')),
    baseUrl = endPoint.substring(0, endPoint.indexOf('/', endPoint.indexOf('/') + 2)),
    dirName = 'sfProject',
    checkLog = 'Please check the logs for details',
    devhub = process.env['SESSION'],
    { gitEmail, gitName } = process.env;

// EXECUTION

try {
    const flags = `
${getFlag('loglevel', loglevel)}
${getFlag('targetdevhubusername', devhub)}
${getFlag('apiversion', apiversion)}
${getStringFlag('name', process.env['packageName'])}
${getStringFlag('path', process.env['path'])}
${getStringFlag('packagetype', packagetype)}
${getBooleanFlag('nonamespace', nonamespace)}
${getBooleanFlag('orgdependent', orgdependent)}
${getStringFlag('description', process.env['description'])}
${getStringFlag('errornotificationusername', errornotificationusername)}
`;
    mkdirSync(dirName);
    process.chdir(dirName);

    prepareAndCloneRepo();
    configureGit();
    createPackage(flags);
} catch (err) {
    execSync(`${getErrorCmd(err.toString())}`);
}

// SCRIPT FUNCTIONS

function getFlag(flagname, value) {
    return value ? ` --${flagname} ${value}` : '';
}

function getStringFlag(flagname, value) {
    return value ? ` --${flagname} "${value}"` : '';
}

function getBooleanFlag(flagname, value) {
    return value ? ` --${flagname}` : '';
}

function getPackagesList() {
    return JSON.parse(execSync(`sfdx force:package:list -v ${devhub} --json || true`).toString());
}

function prepareAndCloneRepo() {
    execSync(`
        copado -p 'Cloning git repository'
        copado-git-get '${branchName}' || (${getErrorCmd(`Could not checkout branch ${branchName}, ${checkLog}`)})
    `);

    execSync(`
        copado -p 'Checked out branch "${branchName}"'
        copado -p 'Setting instance url'
        sfdx force:config:set instanceUrl=${baseUrl} --global || (${getErrorCmd(`Failed setting an instance URL, ${checkLog}`)})
    `);
}

function createPackage(pkgCreateParam) {
    if(namespaceprefix && !nonamespace && readProjectJson()?.namespace !== namespaceprefix) {
        setProgressStatus({ progressStatus: 'Error', errorMessage: `Namespace in package record does not match with sfdx-project.json file in git repository.` });
    }
    const createCmd = `
        copado -p 'Creating package'
        sfdx force:package:create ${pkgCreateParam.replace(/\n|\r/g, '')} --json || true
        copado -p 'Finishing'
    `;
    logger(`Package Create Command ==> sfdx force:package:create ${pkgCreateParam.replace(/\n|\r/g, '')} --json`);
    const response = JSON.parse(execSync(createCmd).toString());
    logger(`Package Create Command Response ==> ${JSON.stringify(response)}`);
    if (!response?.status && response?.result?.Id) {
        commit();
        const packageId = response.result.Id,
            packagesDetails = getPackagesList();

        if (packagesDetails.status) {
            setProgressStatus({ progressStatus: 'Error', errorMessage: `Error fetching package version list from devhub, ${response.message}` });
        } else {
            const namespace = packagesDetails.result.find(pkg => pkg.Id === packageId)?.NamespacePrefix,
                resultData = { packageId: packageId, namespace: namespace ? namespace : '' };
            setProgressStatus({ progressStatus: 'Package created successfully', resultData: JSON.stringify(resultData) });
        }
    } else if (response?.status && response?.message)
        setProgressStatus({ progressStatus: 'Package creation failed', errorMessage: `Could not create package, ${response.message}` });
    else
        setProgressStatus({
            progressStatus: 'Package creation did not finish',
            resultData: `${JSON.stringify(response)}`,
            errorMessage: `Could not create package, ${response.message}`
        });
}

function commit() {
    execSync(
        `
        git add 'sfdx-project.json' || (${getErrorCmd(`Error adding git change, ${checkLog}`)})
        git commit -m 'Committing sfdx-project.json' || (${getErrorCmd(`Error commiting git change, ${checkLog}`)})
        git push origin ${branchName} || (${getErrorCmd(`Error pushing changes in git, ${checkLog}`)})`,
        { stdio: 'inherit' }
    );
}

function setProgressStatus({ progressStatus, errorMessage, resultData }) {
    execSync(`copado -p '${progressStatus}' ${errorMessage ? `-e '${errorMessage}'` : ''} ${resultData ? `-r '${resultData}'` : ''}`);
    if (errorMessage) {
        throw errorMessage;
    }
}

function getErrorCmd(error) {
    return `copado -p 'Error' -e '${error}' && exit 1`;
}

function readProjectJson() {
	return JSON.parse(readFileSync('sfdx-project.json', 'utf-8'));
}

function logger(text) {
    console.log(text);
}

function configureGit() {
    execSync(`
        git config --local user.email "${gitEmail}" || exit 1
        git config --local user.name "${gitName}" || exit 1`,
        { stdio: 'inherit' }
    );
}

prepareSfdxProject() {
	copado -p "Preparing sfdx project configuration"

	source_endpoint=($(node -p "('$source_endpoint').substring(0, '$source_endpoint'.indexOf('.com') + 4)"))

    mkdir -p .sfdx
	cat << __EOF__ > ".sfdx/sfdx-config.json"
	{
		"instanceUrl": "$source_endpoint"
	}
__EOF__

    cat << __EOF__ > "sfdx-project.json"
	{
		"namespace": "$namespace",
		"packageDirectories": [
			{
				"path": "$source_path",
                "default": true
            }
		],
        "sfdcLoginUrl": ""
	}
__EOF__
}

runApexTests() {
	echo "test classes: ${test_classes}"
    copado -p "Running Apex Tests in the source environment"
    sfdx force:apex:test:run -u ${source_session_id} -t ${test_classes} -r json --detailedcoverage --codecoverage --verbose --wait ${test_minutes_timeout} > /tmp/apex-tests-output.json || true
    jq 'del(.result.summary.username)' /tmp/apex-tests-output.json > tmp.$$.json && mv tmp.$$.json /tmp/apex-tests-output.json
}

attachResultFile() {
    # To function result
	copado -u /tmp/apex-tests-output.json --name "apex-tests-output.json"

	# To test independent results, if existing
	if [ -n "$test_result_ids" ];
    then
      test_result_ids=$(echo $test_result_ids | sed -e "s/\[//g" | sed -e "s/\]//g" | sed -e "s/ //g" | xargs)
      IFS=','
      for test_result_id in ${test_result_ids};
      do
          copado -u /tmp/apex-tests-output.json --name "apex-tests-output.json" --parentid $test_result_id
      done
    fi

    # To consolidated result, if existing
	if [ -n "$consolidated_result_id" ];
    then
	    copado -u /tmp/apex-tests-output.json --name "apex-tests-output.json" --parentid $consolidated_result_id
    fi
}

prepareSfdxProject
runApexTests
attachResultFile

#!/usr/bin/env node

const { readFileSync, mkdirSync } = require("fs"),
	{ execSync } = require("child_process"),
	{ env, chdir } = require("process"),
	checkLog = "Please check the logs for details",
	projectDirectory = "sfProject",
	packageVersion = JSON.parse(env.packageVersion);

// EXECUTION

try {
	mkdirSync(projectDirectory);
	chdir(projectDirectory);
	cloneRepo(packageVersion.copado__Branch__c);
	execSync(`copado -p 'Fetching details from repository'`);
	const result = getVersionDetailsResult(readProjectJson());

	execSync(
		`copado -p "Fetch Successful" -r '${result}' && exit 0`,
		{ stdio: "inherit" }
	);
} catch (err) {
	execSync(`${getErrorCmd(err.toString())}`);
}

// SCRIPT FUNCTIONS

function cloneRepo(branchName) {
	execSync(
		`
		copado -p 'Cloning git repository'
		copado-git-get ${branchName} --depth 1 || (${getErrorCmd(
			`Error check out branch, ${checkLog}`
		)})`,
		{ stdio: "inherit" }
	);
}

function readProjectJson() {
	return JSON.parse(readFileSync("sfdx-project.json", "utf-8"));
}

function getErrorCmd(error) {
	return `copado -p 'Error' -e "${error}" && exit 1`;
}

function getVersionDetailsResult(projectJson) {
	const packageId = packageVersion?.copado__Artifact__r?.copado__Package_Id__c,
		packageAliases = projectJson?.packageAliases,
		recordNamespace = packageVersion.copado__Artifact__r?.copado__Package_Namespace__c,
		projectJsonNamespace = projectJson?.namespace,
		pkgName = Object.keys(packageAliases).find(pckgId => packageAliases[pckgId] === packageId);

	checkAttributesAndHandleErrors({ packageAliases, packageId, pkgName, recordNamespace, projectJsonNamespace });
	const pkgDirectory = projectJson?.packageDirectories.find(
		dir => dir.package === pkgName
	),
		{ versionNumber, versionDescription, versionName, postInstallUrl, ancestorVersion, ancestorId, postInstallScript, releaseNotesUrl, uninstallScript, definitionFile } = pkgDirectory;


	if (!pkgDirectory) {
		execSync(
			`${getErrorCmd(`Could not find pkgDirectory attribute in project JSON file.`)}`
		);
	}
	return JSON.stringify({
		packageVersion: {
			Id: packageVersion.Id,
			copado__Version_number__c: versionNumber,
			copado__Version_Description__c: versionDescription,
			copado__Version_Name__c: versionName,
			Post_Install_URL__c: postInstallUrl,
			Post_Install_Script__c: postInstallScript,
			Release_Notes_URL__c: releaseNotesUrl,
			Uninstall_Script__c: uninstallScript,
			copado__DefinitionFile__c: definitionFile,
			Ancestor_Version__c: ancestorVersion,
			Ancestor_Id__c: packageAliases[ancestorId]
		}
	});
}

function checkAttributesAndHandleErrors({ packageAliases, packageId, pkgName, recordNamespace, projectJsonNamespace }) {
	if (!packageAliases) {
		execSync(
			`${getErrorCmd(`Missing attribute packageAliases from project JSON file`)}`
		);
	}

	if (!packageId || !pkgName) {
		execSync(
			`${getErrorCmd(`Missing associated package either in Salesforce record or project JSON file.`)}`
		);
	}

	if (recordNamespace && projectJsonNamespace && recordNamespace != projectJsonNamespace) {
		execSync(
			`${getErrorCmd(`Mismatch of namespace in Package record and repository.`)}`
		);
	}
}

