#!/usr/bin/env node
/**
 * Performs deployment of custom setting records.
 * @param source_sessionid
 * @param source_endpoint
 * @param source_env_var
 * @param destination_env_var
 * @param destination_sessionid
 * @param destination_endpoint
 * @param records
 * @param custom_setting_name
 * @param profiles
 * @param users
 * @param organizations
 * @param key_prefix
 * @param isValidation
 */

const fs = require('fs'),
    { execSync, exec } = require('child_process'),
    {
        source_sessionid,
        source_endpoint,
        source_env_var,
        destination_env_var,
        destination_sessionid,
        destination_endpoint,
        custom_setting_name,
        isValidation
    } = process.env,
    sourceApiVersion = '55.0',
    source_base_url = getBaseUrl(source_endpoint),
    destination_base_url = getBaseUrl(destination_endpoint),
    source_environment = 'source',
    destination_environment = 'destination';

let { records, profiles, users, organizations, key_prefix } = process.env,
    profileQuery,
    userQuery,
    organizationQuery,
    recordNames,
    sourceCustomSetting = {},
    sourceRecords = {
        profileMapping: {},
        userMapping: {},
        organizationMapping: {},
        records: []
    },
    destinationRecords = {
        profileMapping: {},
        userMapping: {},
        organizationMapping: {},
        records: {}
    };

// EXECUTION

execute();

// FUNCTIONS

function execute() {
    try {
        validateExecutionMode();
        setup();
        validateSource();
        prepareQueryForDependentRecords();
        Promise.all(getCustomSettingDependentData(source_sessionid, `Retrieve records from ${source_environment}`))
            .then((data) => {
                queryCustomSettingInSource(data);
                validateDestination();
                Promise.all(getCustomSettingDependentData(destination_sessionid, `Retrieve records from ${destination_environment}`))
                    .then((data) => {
                        queryCustomSettingInDestination(data);
                        mergeAndDeployRecords();
                    })
                    .catch((error) => {
                        logError(error);
                    });
            })
            .catch((error) => {
                logError(error);
            });
    } catch (error) {
        logError(error);
    }
}

function getBaseUrl(endpoint) {
    return endpoint.substring(0, endpoint.indexOf('/', endpoint.indexOf('/') + 2));
}

function validateExecutionMode() {
    const validationModeMessage = 'This step will not be executed for validate changes';
    if (isValidation === 'true') {
        execSync(`copado -p '${validationModeMessage}' --result-data '${validationModeMessage}'`);
        process.exit(0);
    }
}

function logError(error) {
    execSync(`copado -p 'Error' -e '${error.toString()}' && exit 1`);
}

function setup() {
    const cmd = `
 copado -p 'Preparing sfdx project configuration'
 cd /app
 sfdx force:project:create --projectname 'sfdx_project' --json
 `;
    execSync(cmd, { stdio: 'inherit' });
    let projectJson = fs.readFileSync('/app/sfdx_project/sfdx-project.json')?.toString();
    if (projectJson) {
        projectJson = JSON.parse(projectJson);
        if(projectJson.sourceApiVersion !== sourceApiVersion) {
        	projectJson = { ...projectJson, sourceApiVersion };
        	fs.writeFileSync('/app/sfdx_project/sfdx-project.json', JSON.stringify(projectJson));
        }
    }
}

function setInstanceUrl(url, message) {
    const cmd = `
 copado -p '${message}'
 cd /app/sfdx_project
 sfdx config:set instanceUrl=${url}
 `;
    execSync(cmd, { stdio: 'inherit' });
}

function retrieveCustomSettingMetadata(sessionid, message, environment) {
    let result;
    if (!custom_setting_name) {
        throw `Error occured (${environment}), invalid custom setting name. Please check logs for more details.`;
    }
    const cmd = `
 copado -p '${message}'
 cd /app/sfdx_project
 sfdx force:source:retrieve --metadata CustomObject:${custom_setting_name} -u ${sessionid} --json`;
    const response = execSync(cmd);
    if (!response) {
        throw `Error occured (${environment}), custom setting metadata retrieval failed. Please check logs for more details.`;
    } else {
        result = extractCustomSettingMetadata(JSON.parse(response.toString()), environment);
    }
    return result;
}

function extractCustomSettingMetadata(metadataResponse, environment) {
    const result = { fields: [], filePath: '' };
    const metadata = metadataResponse?.result?.inboundFiles;
    if (metadataResponse.status != 0 || !metadata) {
        throw `Error occured (${environment}), '${JSON.stringify(metadataResponse)}'. Please check logs for more details.`;
    } else {
        metadata.forEach((element) => {
            if (element.error) {
                throw `Error occured (${environment}), '${JSON.stringify(element.error)}'. Please check logs for more details.`;
            } else if (element.type === 'CustomField') {
                result.fields.push(element.fullName.replace(`${custom_setting_name}.`, ''));
            } else if (element.type === 'CustomObject') {
                result.filePath = element.filePath;
            }
        });
    }
    return result;
}

function checkCustomSettingTypeAndVisibility(customSettingPath, message, environment) {
    const result = { isHierarchy: false };
    if (!customSettingPath) {
        throw `Error occured (${environment}), invalid custom setting filePath. Please check logs for more details.`;
    }
    const cmd = `
 copado -p '${message}'
 cd /app/sfdx_project
 cat ${customSettingPath}
 `;
    let response = execSync(cmd);
    if (!response) {
        throw `Error occured (${environment}), custom setting details not found. Please check logs for more details.`;
    } else {
        response = response.toString();
        if (!response.includes('<visibility>Public</visibility>')) {
            throw `Error occured (${environment}), '${JSON.stringify(
                response
            )}'. Only public custom setting records can be moved from source org to destination org. Please check logs for more details.`;
        }
        result.isHierarchy = response.includes('<customSettingsType>Hierarchy</customSettingsType>');
    }
    return result;
}

function validateSource() {
    setInstanceUrl(source_base_url, `Setting instance url to ${source_environment}`);
    const metadata = retrieveCustomSettingMetadata(
        source_sessionid,
        `Retrieve and validate custom setting in ${source_environment}`,
        source_environment
    );
    Object.assign(sourceCustomSetting, metadata);
    const type = checkCustomSettingTypeAndVisibility(
        sourceCustomSetting.filePath,
        `Checking custom setting type and visibility in ${source_environment}`,
        source_environment
    );
    Object.assign(sourceCustomSetting, type);
}

function prepareQueryForDependentRecords() {
    const hierarchyCustomSettingError = `Error occured, for hierarchy custom setting we need profile, user or organization record(s) to deploy the custom setting data. Please check logs for more details.`;
    const listCustomSettingError = `Error occured, no custom setting record(s) found for deployment. Please check logs for more details.`;
    if (sourceCustomSetting.isHierarchy) {
        if (!profiles && !users && !organizations) {
            throw hierarchyCustomSettingError;
        }
        profiles = JSON.parse(profiles);
        users = JSON.parse(users);
        organizations = JSON.parse(organizations);
        if (profiles?.length) {
            profileQuery = `SELECT Id, Name FROM Profile WHERE Name IN ('${profiles.map((profile) => profile.Name).join("','")}')`;
        }
        if (users?.length) {
            const userNames = [];
            const userProfileNames = [];
            users.forEach((user) => {
                userNames.push(user.Name);
                userProfileNames.push(user.Profile.Name);
            });
            userQuery = `SELECT Id, Name FROM User WHERE Name IN ('${userNames.join("','")}') AND Profile.Name IN ('${userProfileNames.join(
                "','"
            )}')`;
        }
        if (organizations?.length) {
            organizationQuery = `SELECT Id, Name FROM Organization WHERE Name IN ('${organizations
                .map((organization) => organization.Name)
                .join("','")}')`;
        }
        if (!profileQuery && !userQuery && !organizationQuery) {
            throw hierarchyCustomSettingError;
        }
    } else if (!records) {
        throw listCustomSettingError;
    } else {
        records = JSON.parse(records);
        if (!records?.length) {
            throw listCustomSettingError;
        } else {
            recordNames = records.map((record) => record.Name);
        }
    }
}

function queryCustomSettingInSource(data) {
    const filterResponse = prepareCustomSettingQueryFilter(data, sourceCustomSetting.isHierarchy, 'Id', 'Name', source_environment);
    Object.assign(sourceRecords, filterResponse);
    delete sourceRecords.customSettingNames;
    const queryResponse = queryCustomSettingRecords(
        sourceCustomSetting.isHierarchy,
        Object.keys(sourceRecords.profileMapping),
        Object.keys(sourceRecords.userMapping),
        Object.keys(sourceRecords.organizationMapping),
        filterResponse.customSettingNames,
        `Error occured, no filters avaliable to query custom seting records in ${source_environment}. Please check logs for more details.`,
        sourceCustomSetting.fields,
        source_sessionid,
        source_environment
    );
    sourceRecords.records = getCustomSettingRecords(
        JSON.parse(queryResponse),
        source_environment,
        `Error occured, there are no custom setting records available in ${source_environment} as per the mentioned filters. Please check logs for more details.`
    );
}

function getCustomSettingRecords(data, environment, noRecordsError) {
    let result;
    if (data.status != 0) {
        throw `Error occured (${environment}), '${JSON.stringify(data)}' Please check logs for more details.`;
    } else {
        result = data?.result?.records;
        if (!records?.length && noRecordsError) {
            throw noRecordsError;
        }
    }
    return result;
}

function prepareCustomSettingQueryFilter(data, isHierarchy, key, value, environment) {
    const result = { profileMapping: {}, userMapping: {}, organizationMapping: {}, customSettingNames: null };
    data.forEach((response) => {
        if (isHierarchy) {
            response = JSON.parse(response);
            if (response.status != 0) {
                throw `Error occured (${environment}), '${JSON.stringify(response)}'. Please check logs for more details.`;
            } else {
                const records = response?.result?.records;
                if (records?.length) {
                    mapRecords(records, result, key, value);
                }
            }
        } else {
            result.customSettingNames = response;
        }
    });
    return result;
}

function queryCustomSettingRecords(
    isHierarchy,
    profileIds,
    userIds,
    orgIds,
    customSettingNames,
    noFilterError,
    fieldsToQuery,
    sessionid,
    environment
) {
    let response;
    let filters = [];
    if (isHierarchy) {
        filters = filters.concat(profileIds, userIds, orgIds);
    } else {
        filters = filters.concat(customSettingNames);
    }

    if (!filters?.length) {
        if (noFilterError) {
            throw noFilterError;
        }
    } else {
        const filterIds = `('${filters.join("','")}')`;
        const query = `SELECT Id, Name, SetupOwnerId ${
            fieldsToQuery?.length ? ',' + fieldsToQuery.join(',') : ''
        } FROM ${custom_setting_name} WHERE ${isHierarchy ? 'SetupOwnerId IN ' : 'Name IN '} ${filterIds}`;
        response = doSoqlSyncQuery(query, sessionid);
    }

    if (!response) {
        throw `Error occured (${environment}), force:data:soql:query command failed. Please check logs for more details.`;
    }
    return response;
}

function mapRecords(records, dataSet, key, value) {
    records.forEach((record) => {
        switch (record?.attributes?.type) {
            case 'Profile':
                dataSet.profileMapping[record[key]] = record[value];
                break;
            case 'User':
                dataSet.userMapping[record[key]] = record[value];
                break;
            case 'Organization':
                dataSet.organizationMapping[record[key]] = record[value];
                break;
        }
    });
}

function getCustomSettingDependentData(sessionId, message) {
    exec(`copado -p '${message}'`, { stdio: 'inherit' });
    const promises = [];
    if (sourceCustomSetting.isHierarchy) {
        [profileQuery, userQuery, organizationQuery].forEach((query) => {
            if (query) {
                promises.push(
                    new Promise((resolve, reject) => {
                        doSoqlAsyncQuery(resolve, reject, query, sessionId);
                    })
                );
            }
        });
    } else {
        promises.push(
            new Promise((resolve, reject) => {
                resolve(recordNames);
            })
        );
    }
    return promises;
}

function doSoqlAsyncQuery(resolve, reject, query, sessionId) {
    exec(getQueryCmd(query, sessionId), (error, data, stderr) => {
        if (error) {
            reject(error);
        } else {
            resolve(data);
        }
    });
}

function doSoqlSyncQuery(query, sessionId) {
    const response = execSync(getQueryCmd(query, sessionId));
    if (!response) {
        throw `Error occured, force:data:soql:query command failed. Please check logs for more details.`;
    } else {
        return response.toString();
    }
}

function getQueryCmd(query, sessionId) {
    return `
 cd /app/sfdx_project
 sfdx force:data:soql:query --query "${query}" --json -u ${sessionId}
 `;
}

function validateDestination() {
    setInstanceUrl(destination_base_url, `Setting instance url to ${destination_environment}`);
    const metadata = retrieveCustomSettingMetadata(
        destination_sessionid,
        `Retrieve and validate custom setting in ${destination_environment}`,
        destination_environment
    );
    const missingFields = sourceCustomSetting.fields.filter((field) => metadata.fields.indexOf(field) === -1);
    if (missingFields?.length) {
        throw `Error occured, fields missing in ${destination_environment} ${missingFields.join(',')}. Please check logs for more details.`;
    }
    const type = checkCustomSettingTypeAndVisibility(
        metadata.filePath,
        `Checking custom setting type and visibility in ${destination_environment}`,
        destination_environment
    );
    if (sourceCustomSetting.isHierarchy != type.isHierarchy) {
        throw `Error occured, there is a mismatch in the custom setting type for source and destination. Please check logs for more details.`;
    }
}

function queryCustomSettingInDestination(data) {
    const filterResponse = prepareCustomSettingQueryFilter(data, sourceCustomSetting.isHierarchy, 'Name', 'Id', destination_environment);
    Object.assign(destinationRecords, filterResponse);
    delete destinationRecords.customSettingNames;
    const queryResponse = queryCustomSettingRecords(
        sourceCustomSetting.isHierarchy,
        Object.values(destinationRecords.profileMapping),
        Object.values(destinationRecords.userMapping),
        Object.values(destinationRecords.organizationMapping),
        filterResponse.customSettingNames,
        null,
        null,
        destination_sessionid,
        destination_environment
    );
    const records = getCustomSettingRecords(JSON.parse(queryResponse), destination_environment, null);
    records.forEach((record) => {
        destinationRecords.records[sourceCustomSetting.isHierarchy ? record.SetupOwnerId : record.Name] = record.Id;
    });
}

function mergeAndDeployRecords() {
    const finalRecords = mergeRecords();
    createCSV(finalRecords);
    varreplace();
    deployRecords();
}

function mergeRecords() {
    const finalRecords = [];
    key_prefix = JSON.parse(key_prefix);
    let destinationSetupOwnerId;
    sourceRecords.records.forEach((record) => {
        if (sourceCustomSetting.isHierarchy) {
            destinationSetupOwnerId = null;
            switch (key_prefix[record.SetupOwnerId.substring(0, 3)]) {
                case 'Profile':
                    destinationSetupOwnerId = destinationRecords.profileMapping[sourceRecords.profileMapping[record.SetupOwnerId]];
                    break;
                case 'User':
                    destinationSetupOwnerId = destinationRecords.userMapping[sourceRecords.userMapping[record.SetupOwnerId]];
                    break;
                case 'Organization':
                    destinationSetupOwnerId =
                        destinationRecords.organizationMapping[sourceRecords.organizationMapping[record.SetupOwnerId]];
                    break;
            }
            if (!destinationSetupOwnerId) {
                return;
            }
        } else {
            destinationSetupOwnerId = null;
        }
        delete record.attributes;
        record.SetupOwnerId = destinationSetupOwnerId;
        record.Id = sourceCustomSetting.isHierarchy
            ? destinationRecords.records[destinationSetupOwnerId]
            : destinationRecords.records[record.Name];
        finalRecords.push(record);
    });
    return finalRecords;
}

function createCSV(finalRecords) {
    if (!finalRecords?.length) {
        throw `No custom setting records avaliable for deployment in ${destination_environment}`;
    } else {
        const csvData = arrayToCSV(finalRecords);
        fs.writeFileSync('/tmp/records.csv', csvData);
    }
}

function arrayToCSV(data) {
    let csv = data.map((row) => Object.values(row));
    csv.unshift(Object.keys(data[0]));
    return `"${csv.join('"\n"').replace(/,/g, '","')}"`;
}

function varreplace() {
    const cmd = `
 copado -p "Replacing environment variables, if any"
 varreplace '${source_env_var}' '/tmp/records.csv' --valuename=true
 varreplace '${destination_env_var}' '/tmp/records.csv' --valuename=false
 `;
    execSync(cmd);
}

function deployRecords() {
    sourceCustomSetting = sourceRecords = destinationRecords = profileQuery = userQuery = organizationQuery = recordNames = null;
    const cmd = `
 cd /app/sfdx_project
 copado -p 'Deploying custom setting records in ${destination_environment}'
 sfdx force:data:bulk:upsert --sobjecttype ${custom_setting_name} --csvfile /tmp/records.csv --externalid Id -u ${destination_sessionid} --wait 60 > /tmp/result.txt
 copado -u /tmp/records.csv --name "records.csv"
 copado -u /tmp/result.txt --name "result.txt"
 `;
    execSync(cmd, { maxBuffer: 50 * 1024 * 1024 });
    const result = fs.readFileSync('/tmp/result.txt');
    if (!result) {
        throw 'Error occured while deploying records in destination, kindly check logs for more details';
    } else if (result.includes('Upsert errors')) {
        throw 'Error occured, kindly check the result.txt file for more details';
    }
}
